  //       { itemName, description, startingBid, closingTime },
  //       { headers: { Authorization: `Bearer ${token}` } } // Send token in headers
  //     );

  //     alert('Auction item posted!');
  //     navigate('/dashboard');
  //   } catch (err) {
  //     alert('Failed to post auction. Please try again.');
  //   }
  // };

  return (
    <div className="form-container">
      <h2>Post New Auction</h2>
      <form className="post_form"onSubmit={handlePostAuction}>
        <input
          type="text"
          placeholder="Item Name"
          value={itemName}
          onChange={(e) => setItemName(e.target.value)}
          required
        />
        <textarea
          placeholder="Item Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>
        <input
          type="number"
          placeholder="Starting Bid"
          value={startingBid}
          onChange={(e) => setStartingBid(e.target.value)}
          required
        />
        <input
          type="datetime-local"
          value={closingTime}
          onChange={(e) => setClosingTime(e.target.value)}
          required
        />
        <button type="submit"className='post-button'>Post Auction</button>
      </form>
    </div>
  );
}

export default PostAuction;
